SELECT Medarbejder.ID,Medarbejder.Fornavn,Medarbejder.Efternavn,Sallery.Navn,Sallery.amount,Dept.amountFROM Medarbejder,Sallery,DeptWHERE Medarbejder.ID = 1 AND Dept.PersonID=1 AND Sallery.SalleryID = 1;CREATE TABLE Medarbejder(ID int NOT NULL PRIMARY KEY,Fornavn varchar(255),Efternavn varchar(255),SalleryID int FOREIGN KEY REFERENCES Sallery(SalleryID),);CREATE TABLE Sallery(SalleryID int NOT NULL PRIMARY KEY,Navn varchar(255),amount int,)DROP TABLE Medarbejder, Sallery, Dept;CREATE TABLE Dept(PersonID int NOT NULL PRIMARY KEY,amount int,)INSERT INTO Medarbejder (ID, Fornavn, Efternavn, SalleryID)VALUES (5, 'Jude', 'Tude', 3)SELECT Medarbejder.ID,Medarbejder.Fornavn,Medarbejder.Efternavn,Sallery.Navn,Sallery.amount,Dept.amountFROM Medarbejder,Sallery,DeptWHERE Medarbejder.ID = 5 AND Dept.PersonID=5 AND Sallery.SalleryID = 3;